CREATE DATABASE IF NOT EXISTS `prime_prototipo_full30` DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;
USE `prime_prototipo_full30`;
CREATE TABLE IF NOT EXISTS `movies` ( id int NOT NULL AUTO_INCREMENT, title varchar(255) NOT NULL, filename varchar(255) NOT NULL, thumb varchar(255) DEFAULT '', description text, rating varchar(20) DEFAULT '', hero varchar(255) DEFAULT '', category varchar(120) DEFAULT '', created_at timestamp NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY (id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;
